        />
        <input
          type="text"
          value={formData.qaz}
          name="qaz"
          onChange={handelFormData}
          placeholder="Search qaz .. "
        />
        <input
          type="submit"
          value="Search"
          className="btn btn-dark btn-block"
        />
      </form>
    </div>
  );
};

