import React, { Component } from "react";
import axios from "axios";

import Search from "./Search";
import UserItem from "./UserItem";
import Spinner from "../Layouts/Spinner";
import Alert from "../Layouts/Alert";

class Users extends Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [],
      loading: false,
      alert: null,
    };
  }

  async componentDidMount() {
    try {
      this.setState({ loading: true });
      let { data } = await axios.get("https://api.github.com/users");
      this.setState({ users: data, loading: false });
    } catch (error) {
      this.setState({ loading: false });
      console.error("Error on api call");
    }
  }

  searchUsers = async (text) => {
    try {
      this.setState({ loading: true });
      let { data } = await axios.get(
        `https://api.github.com/search/users?q=${text}`
      );
      this.setState({ users: data.items, loading: false });
    } catch (error) {
      this.setState({ loading: false });
      console.error(error);
    }
  };

  diplayAlert = (msg, type) => {
    this.setState({ alert: { msg, type } });
    setTimeout(() => {
      this.setState({ alert: null });
    }, 5000);
  };
  removeNonMatch = (keyword) => {
    let users = this.state.users;
    let regex = `/${keyword}/g`;
    let newUsers = users.filter((user) => {
      let name = user.login;
      return name.test(regex);
    });
    console.log(newUsers);
  };

  render() {
    const userStyle = {
      display: "grid",
      gridTemplateColumns: "repeat(3,1fr)",
      gridGap: "1rem",
    };
    return (
      <div>
        {this.state.alert && (
          <Alert type={this.state.alert.type} msg={this.state.alert.msg} />
        )}
        <Search
          onNullSearch={this.diplayAlert}
          onSearchFilter={this.removeNonMatch}
          searchUsers={this.searchUsers}
        />
        {this.state.loading ? (
          <Spinner />
        ) : (
          <div style={userStyle}>
            {this.state.users.map((element) => (
              <UserItem
                login={element.login}
                avatar_url={element.avatar_url}
                key={element.id}
              />
            ))}
          </div>
        )}
      </div>
    );
  }
}

export default Users;
