  const handleCount1 = (e) => {
    setCount1(count1 + 1);
  };
  const handleCount2 = (e) => {
    setCount2(count2 + 1);
  };

  return (
    <div>
      <h1>Contact Us</h1>
      <p>admin@hishamtk.com</p>
      <button className="btn" onClick={handleCount1}>
        {countData.count1}
      </button>
      <button className="btn" onClick={handleCount2}>
        {countData.count2}
      </button>
    </div>
  );
}

export default Contact;
