import axios from "axios";
import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";

import "./App.css";

import About from "./Components/Layouts/About";
import Contact from "./Components/Layouts/Contact";
import Navbar from "./Components/Layouts/Navbar";
import Profile from "./Components/Users/Profile";
import Users from "./Components/Users/Users";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { loading: false, users: [], user: [], repo: [] };
  }

  getAllUsers = async () => {
    try {
      this.setState({ loading: true });
      let res = await axios.get("https://api.github.com/users");
      this.setState({ loading: false, users: res.data });
    } catch (error) {
      console.error("error on api call", error);
      this.setState({ loading: false });
    }
  };

  searchUsers = async (text) => {
    try {
      this.setState({ loading: true });
      let { data } = await axios.get(
        `https://api.github.com/search/users?q=${text}`
      );
      this.setState({ users: data.items, loading: false });
    } catch (error) {
      this.setState({ loading: false });
      console.error(error);
    }
  };

  getUserAndRepo = async (user) => {
    try {
      this.setState({ loading: true });
      let data = axios.get(`https://api.github.com/users/${user}`);
      this.setState({ user: data });
      let res = await axios.get(
        `https://api.github.com/users/${user}/repos?per_page=10`
      );
      this.setState({ repos: res.data, loading: false });
    } catch (error) {
      this.setState({ loading: false });
    }
  };

  render() {
    return (
      <div className="App">
        <Navbar title="Github App" icon="fab fa-github" />
        <div className="container">
          {" "}
          <Switch>
            <Route exact path="/">
              {" "}
              <Users
                getAllUsers={this.getAllUsers}
                users={this.state.users}
                loading={this.state.loading}
                searchUsers={this.searchUsers}
              />
            </Route>
            <Route exact path="/about">
              <About />
            </Route>
            <Route exact path="/contact">
              <Contact />
            </Route>
            <Route exact path="/user/:userId" component={Profile} />
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
