import axios from "axios";
import React, { Component } from "react";
import { Route, Switch } from "react-router-dom";

import "./App.css";

import About from "./Components/Layouts/About";
import Alert from "./Components/Layouts/Alert";
import Contact from "./Components/Layouts/Contact";
import Navbar from "./Components/Layouts/Navbar";
import Profile from "./Components/Users/Profile";
import Users from "./Components/Users/Users";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      users: [],
      user: [],
      repos: [],
      alert: null,
    };
  }

  getAllUsers = async () => {
    try {
      this.setState({ loading: true });
      let res = await axios.get("https://api.github.com/users");
      this.setState({ loading: false, users: res.data });
    } catch (error) {
      console.error("error on api call", error);
      this.setState({ loading: false });
    }
  };

  handleAlert = (msg,type) =>{
    this.setState({ alert: { msg, type } });
    setTimeout(() => {
      this.setState({ alert: null });
    }, 5000);
  }

  setAlert = (msg, type) => {
    this.setState({ alert: { msg, type } });
    setTimeout(() => {
      this.setState({ alert: null });
    }, 5000);
  };

  searchUsers = async (text) => {
    try {
      if (text === "") {
        return this.setAlert("Search should not be empty", "danger");
      }
      this.setState({ loading: true });
      let { data } = await axios.get(
        `https://api.github.com/search/users?q=${text}`
      );
      this.setState({ users: data.items, loading: false });
    } catch (error) {
      this.setState({ loading: false });
      console.error(error);
    }
  };

  getUserAndRepo = async (user) => {
    try {
      this.setState({ loading: true });
      let { data } = await axios.get(`https://api.github.com/users/${user}`);
      this.setState({ user: data });
      let res = await axios.get(
        `https://api.github.com/users/${user}/repos?per_page=10`
      );
      this.setState({ repos: res.data, loading: false });
    } catch (error) {
      this.setState({ loading: false });
      console.log(error);
    }
  };

  render() {
    return (
      <div className="App">
        <Navbar title="Github App" icon="fab fa-github" />

        <div className="container">
          <Alert alert={this.state.alert} />
          <Switch>
            <Route exact path="/">
              <Users
                getAllUsers={this.getAllUsers}
                users={this.state.users}
                loading={this.state.loading}
                searchUsers={this.searchUsers}
                alert={this.state.alert}
                han
              />
            </Route>
            <Route exact path="/about">
              <About />
            </Route>
            <Route exact path="/contact">
              <Contact />
            </Route>
            <Route
              exact
              path="/user/:userId"
              render={(props) => (
                <Profile
                  loading={this.state.loading}
                  getUserAndRepo={this.getUserAndRepo}
                  user={this.state.user}
                  repos={this.state.repos}
                  {...props}
                />
              )}
            />
          </Switch>
        </div>
      </div>
    );
  }
}

export default App;
