import axios from "axios";
import React, { Component } from "react";
import { Link } from "react-router-dom";
import Repos from "./Repos";
import Spinner from "../Layouts/Spinner";

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = { user: {}, loading: false, repos: [] };
  }

  async componentDidMount() {
    const { userId } = this.props.match.params;
    try {
      let { data } = await axios.get(`https://api.github.com/users/${userId}`);
      this.setState({ user: data });
      let res = await axios.get(
        `https://api.github.com/users/${userId}/repos?per_page=10`
      );
      this.setState({ repos: res.data });
    } catch (error) {
      console.log("api call error", error);
    }
  }

  render() {
    return (
      <>
        {this.state.loading ? (
          <Spinner />
        ) : (
          <div>
            <Link to="/" className="btn btn-light">
              Back to search
            </Link>
            Hireable :
            {this.state.user.hireable ? (
              <i className="fas fa-check" text-success />
            ) : (
              <i className="fas fa-times-circle" text-danger />
            )}
            <div className="card grid-2">
              <div className="all-center">
                <img
                  src={this.state.user.avatar_url}
                  alt={this.state.user.login}
                  className="round-img"
                  style={{ width: "150px" }}
                />
                <h2>{this.state.user.login}</h2>
                <p>Location : {this.state.user.location}</p>
              </div>
              <div>
                {this.state.user.bio && (
                  <>
                    <h3>Bio</h3>
                    <p>{this.state.user.bio}</p>
                  </>
                )}
                <a
                  href={this.state.user.html_url}
                  className="btn btn-dark my-1"
                  rel="noreferrer"
                >
                  Github Profile
                </a>
                <ul>
                  <li>
                    {this.state.user.login && (
                      <>
                        <strong>Username :</strong>
                        {this.state.user.login}
                      </>
                    )}
                  </li>
                  <li>
                    {this.state.user.company && (
                      <>
                        <strong>Company :</strong>
                        {this.state.user.company}
                      </>
                    )}
                  </li>
                  <li>
                    {this.state.user.blog && (
                      <>
                        <strong>Blog :</strong>
                        {this.state.user.blog}
                      </>
                    )}
                  </li>
                </ul>
              </div>
            </div>
            <div className="card text-center">
              <div className="badge badge-primary">
                Followers :{this.state.user.followers}
              </div>
              <div className="badge badge-success">
                Following :{this.state.user.following}
              </div>
              <div className="badge badge-light">
                Public Repos :{this.state.user.public_repos}
              </div>
              <div className="badge badge-primary">
                Public Gists : {this.state.user.public_gists}
              </div>
            </div>
            <Repos repos={this.state.repos} />
          </div>
        )}
      </>
    );
  }
}

export default Profile;
